mapper.select:
    select  from T_BUSINESS_TRANS 
   1=1	
         and TRANS_USER_TOKEN = #{transUserToken,jdbcType=VARCHAR}	    	
         and TRANS_INSURE_ID = #{transInsureId,jdbcType=VARCHAR}	    	
	     and TRANS_ID = #{transId,jdbcType=VARCHAR}
		 and BUSINESS_ID = #{businessId,jdbcType=VARCHAR}
		 and TRANS_NO = #{transNo,jdbcType=VARCHAR}	
		 and STATUS = #{status,jdbcType=VARCHAR}	
		 and TRANS_TYPE = #{transType,jdbcType=VARCHAR}
		 and CREATE_DATE = #{createDate,jdbcType=DATE}
		 and UPDATE_DATE = #{updateDate,jdbcType=DATE}
		 and TRANS_TYPE = #{transType,jdbcType=DATE}
		
			
				AND ISVALID = #{isvalid,jdbcType=VARCHAR}
			
			
				AND ISVALID = '1'

   select count(*) from T_BUSINESS_TRANS 
   1=1
		 and TRANS_ID = #{transId,jdbcType=VARCHAR}
		 and BUSINESS_ID = #{businessId,jdbcType=VARCHAR}
		 and TRANS_NO = #{transNo,jdbcType=VARCHAR}	
		 and STATUS = #{status,jdbcType=VARCHAR}	
		 and CREATE_DATE = #{createDate,jdbcType=DATE}
		 and UPDATE_DATE = #{updateDate,jdbcType=DATE}
		
			
				AND ISVALID = #{isvalid,jdbcType=VARCHAR}
			
			
				AND ISVALID = '1'

mapper.update:
update T_BUSINESS_TRANS
ID = #{id,jdbcType=DECIMAL}, BUSINESS_ID = #{businessId,jdbcType=VARCHAR}, TRANS_NO = #{transNo,jdbcType=VARCHAR}, TRANS_TYPE = #{transType,jdbcType=VARCHAR}, TRANS_CHANNEL = #{transChannel,jdbcType=VARCHAR}, TRANS_TIME = #{transTime,jdbcType=DATE}, PAY_MONEY = #{payMoney,jdbcType=DECIMAL}, STATUS = #{status,jdbcType=VARCHAR}, RESULT_MSG = #{resultMsg,jdbcType=VARCHAR}, ISVALID = #{isvalid,jdbcType=VARCHAR}, CREATE_DATE = #{createDate}, UPDATE_DATE = #{updateDate}, TRANS_INSURE_ID = #{transInsureId,jdbcType=VARCHAR}, TRANS_USER_TOKEN = #{transUserToken,jdbcType=VARCHAR}, TRANS_PRODUCT_NAME = #{transProductName,jdbcType=VARCHAR},
where ID = #{id,jdbcType=VARCHAR} AND TRANS_USER_TOKEN=#{transUserToken,jdbcType=VARCHAR}
