mapper.select:
    select  from T_USER_ACCOUNT a
   1=1		    	
	     and USER_ACCOUNT_ID = #{userAccountId,jdbcType=VARCHAR}
		 and USER_ID = #{userId,jdbcType=VARCHAR}
		 and VIRTUAL_ACCOUNT_NO = #{virtualAccountNo,jdbcType=VARCHAR}	
		 and STATUS = #{status,jdbcType=VARCHAR}	
		 and CREATE_DATE = #{createDate}
		 and UPDATE_DATE = #{updateDate}
		
			
				AND ISVALID = #{isvalid,jdbcType=VARCHAR}
			
			
				AND ISVALID = '1'

   select count(*) from T_USER_ACCOUNT a
   1=1
		 and USER_ACCOUNT_ID = #{userAccountId,jdbcType=VARCHAR}
		 and USER_ID = #{userId,jdbcType=VARCHAR}
		 and VIRTUAL_ACCOUNT_NO = #{virtualAccountNo,jdbcType=VARCHAR}	
		 and STATUS = #{status,jdbcType=VARCHAR}	
		 and CREATE_DATE = #{createDate,jdbcType=TIMESTAMP}
		 and UPDATE_DATE = #{updateDate,jdbcType=TIMESTAMP}
		
			
				AND ISVALID = #{isvalid,jdbcType=VARCHAR}
			
			
				AND ISVALID = '1'

mapper.update:
UPDATE T_USER_ACCOUNT
bind_card_flag = #{bindCardFlag,jdbcType=VARCHAR}, STATUS ="31",UPDATE_DATE = NOW()
WHERE bind_card_flow_no = #{bindFlowNo,jdbcType=VARCHAR}

update T_USER_ACCOUNT
bind_card_flag = #{bindCardFlag,jdbcType=VARCHAR}, bind_card_flow_no = #{bindCardFlowNo,jdbcType=VARCHAR}, credit_card = #{creditCard,jdbcType=VARCHAR}, bind_card_phone = #{bindPhone,jdbcType=VARCHAR}, UPDATE_DATE = NOW()
where USER_ACCOUNT_ID = #{userAccountId,jdbcType=VARCHAR}

	UPDATE T_USER_ACCOUNT

	SET  history_bind_card= CONCAT(credit_card,","), bind_card_flag ="0",credit_card="",bind_card_phone="",UPDATE_DATE =NOW(),STATUS ="30"
	
	WHERE unbind_card_flow_no = #{unbindflowNo,jdbcType=VARCHAR}
