mapper.select:
    select  from T_USER_ACCOUNT_DETAIL 
   1=1		    	
	     and USER_ACCOUNT_DETAIL_ID = #{userAccountDetailId,jdbcType=CHAR}
		 and USER_ACCOUNT_ID = #{userAccountId,jdbcType=CHAR}
		 and BUSINESS_ID = #{businessId,jdbcType=CHAR}	
		 and BUSINESS_NO = #{businessNo,jdbcType=VARCHAR}	
		 and STATUS = #{status,jdbcType=CHAR}	
		 and CREATE_DATE = #{createDate,jdbcType=TIMESTAMP}
		 and UPDATE_DATE = #{updateDate,jdbcType=TIMESTAMP}
		 and APPLY_TIME >= #{beginDate,jdbcType=TIMESTAMP} 
		 and APPLY_TIME < #{endDate,jdbcType=TIMESTAMP} 
		
			
				AND ISVALID = #{isvalid,jdbcType=CHAR}
			
			
				AND ISVALID = '1'

   select count(*) from T_USER_ACCOUNT_DETAIL 
   1=1
		 and USER_ACCOUNT_DETAIL_ID = #{userAccountDetailId,jdbcType=CHAR}
		 and USER_ACCOUNT_ID = #{userAccountId,jdbcType=CHAR}
		 and BUSINESS_ID = #{businessId,jdbcType=CHAR}	
		 and BUSINESS_NO = #{businessNo,jdbcType=VARCHAR}
		 and STATUS = #{status,jdbcType=CHAR}	
		 and CREATE_DATE = #{createDate,jdbcType=TIMESTAMP}
		 and UPDATE_DATE = #{updateDate,jdbcType=TIMESTAMP}
		 and APPLY_TIME >= #{beginDate,jdbcType=TIMESTAMP} 
		 and APPLY_TIME < #{endDate,jdbcType=TIMESTAMP} 
		
			
				AND ISVALID = #{isvalid,jdbcType=CHAR}
			
			
				AND ISVALID = '1'

mapper.update:
update T_USER_ACCOUNT_DETAIL
ID = #{id,jdbcType=DECIMAL}, USER_ACCOUNT_ID = #{userAccountId,jdbcType=CHAR}, BUSINESS_ID = #{businessId,jdbcType=CHAR}, BUSINESS_NO = #{businessNo,jdbcType=VARCHAR}, DETAIL_TYPE = #{detailType,jdbcType=VARCHAR}, DETAIL_IO = #{detailIo,jdbcType=VARCHAR}, FROM_ACCOUNT_NAME = #{fromAccountName,jdbcType=VARCHAR}, FROM_ACCOUNT_NO = #{fromAccountNo,jdbcType=VARCHAR}, FROM_BANK_CODE = #{fromBankCode,jdbcType=VARCHAR}, FROM_BANK_NAME = #{fromBankName,jdbcType=VARCHAR}, TO_ACCOUNT_NAME = #{toAccountName,jdbcType=VARCHAR}, TO_ACCOUNT_NO = #{toAccountNo,jdbcType=VARCHAR}, TO_BANK_CODE = #{toBankCode,jdbcType=VARCHAR}, TO_BANK_NAME = #{toBankName,jdbcType=VARCHAR}, APPLY_TIME = #{applyTime,jdbcType=TIMESTAMP}, END_TIME = #{endTime,jdbcType=TIMESTAMP}, APPLY_AMOUNT = #{applyAmount,jdbcType=DECIMAL}, STATUS = #{status,jdbcType=VARCHAR}, ISVALID = #{isvalid,jdbcType=CHAR}, CREATE_DATE = #{createDate,jdbcType=TIMESTAMP}, UPDATE_DATE = #{updateDate,jdbcType=TIMESTAMP},
where USER_ACCOUNT_DETAIL_ID = #{userAccountDetailId,jdbcType=CHAR}
