mapper.select:
 select  from T_POLICY_MASTER 
1=1               
      and POLICY_ID = #{policyId,jdbcType=CHAR}
      and PRODUCT_ID = #{productId,jdbcType=CHAR}
      and INSURANCE_ID = #{insuranceId,jdbcType=CHAR}
      and USER_ID = #{userId,jdbcType=CHAR}
      and ORDER_NO = #{orderNo,jdbcType=VARCHAR}   
      and PAY_ORDER_NO = #{payOrderNo,jdbcType=VARCHAR}  
      and APPLICANT_ID = #{applicantId,jdbcType=CHAR}
      and INSURED_ID = #{insuredId,jdbcType=CHAR}
      and SEND_CODE = #{sendCode,jdbcType=VARCHAR}   
      and POLICY_CODE = #{policyCode,jdbcType=VARCHAR}       
      and STATUS = #{status,jdbcType=CHAR}   
      and CREATE_DATE = #{createDate,jdbcType=TIMESTAMP}
      and UPDATE_DATE = #{updateDate,jdbcType=TIMESTAMP}
      and APPLY_TIME >= #{beginDate,jdbcType=TIMESTAMP} 
      and APPLY_TIME < #{endDate,jdbcType=TIMESTAMP} 


             AND ISVALID = #{isvalid,jdbcType=CHAR}


             AND ISVALID = '1'

