mapper.update:
update T_POLICY_MASTER
ID = #{id,jdbcType=DECIMAL}, PRODUCT_ID = #{productId,jdbcType=CHAR}, INSURANCE_ID = #{insuranceId,jdbcType=CHAR}, USER_ID = #{userId,jdbcType=CHAR}, ORDER_NO = #{orderNo,jdbcType=VARCHAR}, PAY_ORDER_NO = #{payOrderNo,jdbcType=VARCHAR}, STATUS = #{status,jdbcType=VARCHAR}, APPLY_TIME = #{applyTime,jdbcType=TIMESTAMP}, UNDERWRITIND_TIME = #{underwritindTime,jdbcType=TIMESTAMP}, PAY_TIME = #{payTime,jdbcType=TIMESTAMP}, VALIDATE_DATE = #{validateDate,jdbcType=TIMESTAMP}, EXPIRE_DATE = #{expireDate,jdbcType=TIMESTAMP}, END_TIME = #{endTime,jdbcType=TIMESTAMP}, APPLICANT_ID = #{applicantId,jdbcType=CHAR}, INSURED_ID = #{insuredId,jdbcType=CHAR}, APPLY_NUM = #{applyNum,jdbcType=INTEGER}, TOTAL_PREM = #{totalPrem,jdbcType=DECIMAL}, TOTAL_REVENUE = #{totalRevenue,jdbcType=DECIMAL}, PREM = #{prem,jdbcType=DECIMAL}, REPAY_AMOUNT = #{repayAmount,jdbcType=DECIMAL}, SEND_CODE = #{sendCode,jdbcType=VARCHAR}, POLICY_CODE = #{policyCode,jdbcType=VARCHAR}, COMMENTS = #{comments,jdbcType=VARCHAR}, ISVALID = #{isvalid,jdbcType=CHAR}, CREATE_DATE = #{createDate}, POLICY_URL = #{policyUrl,jdbcType=VARCHAR}, UPDATE_DATE = #{updateDate}, RELATION = #{relation,jdbcType=VARCHAR},
where POLICY_ID = #{policyId,jdbcType=CHAR}

mapper.select:
select a.POLICY_ID,a.APPLY_TIME,a.PAY_TIME,a.VALIDATE_DATE,a.EXPIRE_DATE,a.STATUS,a.POLICY_CODE,a.SEND_CODE,a.TOTAL_PREM,a.TOTAL_REVENUE,
       b.PRODUCT_ID,b.PRODUCT_NAME,b.PRODUCT_PERIOD,b.PERIOD_TYPE,b.PRODUCT_TYPE,b.EXPECT_RATE ,a.APPLICANT_ID,a.INSURED_ID
from T_POLICY_MASTER as a left join  T_PRODUCT as b on a.PRODUCT_ID=b.PRODUCT_ID

     a.POLICY_ID = #{policyId,jdbcType=VARCHAR}
     and a.PRODUCT_ID = #{productId,jdbcType=VARCHAR}
     and a.INSURANCE_ID = #{insuranceId,jdbcType=VARCHAR}
     and a.USER_ID = #{userId,jdbcType=CHAR}
     and a.ORDER_NO = #{orderNo,jdbcType=VARCHAR} 
     and a.PAY_ORDER_NO = #{payOrderNo,jdbcType=VARCHAR}    
     and a.APPLICANT_ID = #{applicantId,jdbcType=CHAR}
     and a.INSURED_ID = #{insuredId,jdbcType=CHAR}
     and a.SEND_CODE = #{sendCode,jdbcType=VARCHAR} 
     and a.POLICY_CODE = #{policyCode,jdbcType=VARCHAR}     
     and a.STATUS = #{status,jdbcType=CHAR} 
     and a.CREATE_DATE = #{createDate,jdbcType=TIMESTAMP}
     and a.UPDATE_DATE = #{updateDate,jdbcType=TIMESTAMP}
     and a.APPLY_TIME >= #{beginDate,jdbcType=TIMESTAMP} 
     and a.APPLY_TIME < #{endDate,jdbcType=TIMESTAMP} 


            AND a.ISVALID = #{isvalid,jdbcType=CHAR}


            AND a.ISVALID = '1'
