mapper.select:
    select  from T_REPAY_APPLY 
   1=1		    	
	     and REPAY_APPLY_ID = #{repayApplyId,jdbcType=CHAR}
		 and APPLY_INFO_ID = #{applyInfoId,jdbcType=CHAR}
		 and REPAY_NO = #{repayNo,jdbcType=VARCHAR}
		 and LOAN_APPLY_ID = #{loanApplyId,jdbcType=CHAR}
		 and POLICY_ID = #{policyId,jdbcType=CHAR}
		 and POLICY_CODE = #{policyCode,jdbcType=VARCHAR}
		 and STATUS = #{status,jdbcType=CHAR}	
		 and CREATE_DATE = #{createDate,jdbcType=TIMESTAMP}
		 and UPDATE_DATE = #{updateDate,jdbcType=TIMESTAMP}
		
			
				AND a.ISVALID = #{isvalid,jdbcType=CHAR}
			
			
				AND a.ISVALID = '1'

   select count(*) from T_REPAY_APPLY 
   1=1
		 and REPAY_APPLY_ID = #{repayApplyId,jdbcType=CHAR}
		 and APPLY_INFO_ID = #{applyInfoId,jdbcType=CHAR}
		 and REPAY_NO = #{repayNo,jdbcType=VARCHAR}
		 and LOAN_APPLY_ID = #{loanApplyId,jdbcType=CHAR}
		 and POLICY_ID = #{policyId,jdbcType=CHAR}
		 and POLICY_CODE = #{policyCode,jdbcType=VARCHAR}
		 and STATUS = #{status,jdbcType=CHAR}	
		 and CREATE_DATE = #{createDate,jdbcType=TIMESTAMP}
		 and UPDATE_DATE = #{updateDate,jdbcType=TIMESTAMP}
		
			
				AND a.ISVALID = #{isvalid,jdbcType=CHAR}
			
			
				AND a.ISVALID = '1'

mapper.update:
update T_REPAY_APPLY
set 
  REPAY_TIME = #{repayTime,jdbcType=TIMESTAMP},
  STATUS = #{status,jdbcType=VARCHAR},
  UPDATE_DATE = #{updateDate,jdbcType=TIMESTAMP}
where REPAY_APPLY_ID = #{repayApplyId,jdbcType=CHAR}
