mapper.select:
    select  from T_PAYMENT_APPLY 
   1=1		    	
	     and PAYMENT_APPLY_ID = #{paymentApplyId,jdbcType=CHAR}
		 and POLICY_ID = #{policyId,jdbcType=CHAR}
		 and USER_ACCOUNT_ID = #{userAccountId,jdbcType=CHAR}
		 and PAY_ORDER_NO = #{payOrderNo,jdbcType=VARCHAR}	
		 and STATUS = #{status,jdbcType=CHAR}	
		 and CREATE_DATE = #{createDate,jdbcType=TIMESTAMP}
		 and UPDATE_DATE = #{updateDate,jdbcType=TIMESTAMP}
		
			
				AND ISVALID = #{isvalid,jdbcType=CHAR}
			
			
				AND ISVALID = '1'

   select count(*) from T_PAYMENT_APPLY 
   1=1
		 and PAYMENT_APPLY_ID = #{paymentApplyId,jdbcType=CHAR}
		 and POLICY_ID = #{policyId,jdbcType=CHAR}
		 and USER_ACCOUNT_ID = #{userAccountId,jdbcType=CHAR}
		 and PAY_ORDER_NO = #{payOrderNo,jdbcType=VARCHAR}	
		 and STATUS = #{status,jdbcType=CHAR}	
		 and CREATE_DATE = #{createDate,jdbcType=TIMESTAMP}
		 and UPDATE_DATE = #{updateDate,jdbcType=TIMESTAMP}
		
			
				AND ISVALID = #{isvalid,jdbcType=CHAR}
			
			
				AND ISVALID = '1'

mapper.update:
update T_PAYMENT_APPLY
ID = #{id,jdbcType=DECIMAL}, POLICY_ID = #{policyId,jdbcType=CHAR}, USER_ACCOUNT_ID = #{userAccountId,jdbcType=CHAR}, PAY_ORDER_NO = #{payOrderNo,jdbcType=VARCHAR}, PAY_TYPE = #{payType,jdbcType=VARCHAR}, THIRDPAY_TYPE = #{thirdpayType,jdbcType=VARCHAR}, FROM_ACCOUNT_NAME = #{fromAccountName,jdbcType=VARCHAR}, FROM_ACCOUNT_NO = #{fromAccountNo,jdbcType=VARCHAR}, FROM_BANK_CODE = #{fromBankCode,jdbcType=VARCHAR}, FROM_BANK_NAME = #{fromBankName,jdbcType=VARCHAR}, TO_ACCOUNT_NAME = #{toAccountName,jdbcType=VARCHAR}, TO_ACCOUNT_NO = #{toAccountNo,jdbcType=VARCHAR}, TO_BANK_CODE = #{toBankCode,jdbcType=VARCHAR}, TO_BANK_NAME = #{toBankName,jdbcType=VARCHAR}, PAY_APPLY_TIME = #{payApplyTime,jdbcType=TIMESTAMP}, PAY_TIME = #{payTime,jdbcType=TIMESTAMP}, PAY_MONEY = #{payMoney,jdbcType=DECIMAL}, STATUS = #{status,jdbcType=VARCHAR}, ISVALID = #{isvalid,jdbcType=CHAR}, CREATE_DATE = #{createDate,jdbcType=TIMESTAMP}, UPDATE_DATE = #{updateDate,jdbcType=TIMESTAMP},
where PAYMENT_APPLY_ID = #{paymentApplyId,jdbcType=CHAR}
